import React from 'react';

const ResidentsList = () => {
    const [residents, updateResidents] = React.useState([]);

    React.useEffect (function effectFunction() {
        fetch('http://localhost/4002/api/residents')
        .then(response => response.json())
        .then(({data:residents}) => {
            updateResidents(residents);
        })
    },[]);

    return (
        <ul>
            residents.map(resident => (
                <li key={residents._id}> {residents}</li>
            ))
        </ul>

    );
}


export default ResidentsList;



