        name="assessorSignature"
        type="field"
        onChange={formik.handleChange}
        value={formik.values.assessorSignature}

        >
        </Sig>
      </Row>


      <div/>
        <div>
          <hr/>
          <button type="submit">Submit</button>
      </div>
    </form>
    </>
  );
};

function ResAssessmentTool() {
  return <SignupForm />;
}

export default ResAssessmentTool;
