import React from 'react';
import axios from 'axios';
import { useFormik } from "formik";
import "../styles.css";


const SignupForm = () => {
  const formik = useFormik({
    initialValues: {residentFirstName:"",
                    residentLastName:"",
                    residentMoveInDate:"",
                    residentDOB: "",
                    residentCottage:"",
                    residentRoomNo: "",
                    residentAhcNo: "",
                    NOKFirstName:"",
                    NOKLastName: "",
                    NOKPhoneNo: "",
                    NOKEmail: "",
                    doctorName: "",
                    doctorClinic: "",
                    doctorEmail: "",
                    },
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
      const residentFirstName = values.residentFirstName;
      const residentLastName = values.residentLastName;
      const residentMoveInDate = values.residentMoveInDate;
      const residentDOB = values.residentDOB;
      const residentCottage = values.residentCottage;
      const residentRoomNo = values.residentRoomNo;
      const residentAhcNo = values.residentAhcNo;
      const NOKFirstName = values.NOKFirstName;
      const NOKLastName = values.NOKLastName;
      const NOKPhoneNo = values.NOKPhoneNo;
      const NOKEmail = values.NOKEmail;
      const doctorName = values.doctorName;
      const doctorClinic = values.doctorClinic;
      const doctorEmail = values.doctorEmail;

      axios({
          method: "POST",
          url: "http://localhost:4001/api/residentadd",
          data: values
        })
      //.then(res => {
      //  console.log(res); // this returns the callback from the post
      //  console.log(values);  // this returns all the values from form
      }
    })

    // now test sending name and email to a route created that will return the data to screen
    // this is a test to be able to see if we can post to database

  return (
    <>
    <h3>Add a New Resident</h3>
    <form onSubmit={formik.handleSubmit}>

    <h5>Personal Details</h5>
    <label>First Name</label>
       <input
          id="residentFirstName"
          name="residentFirstName"
          type="field"
          onChange={formik.handleChange}
          value={formik.values.residentFirstName}
       />

<label>Last Name</label>
       <input
          id="residentLastName"
          name="residentLastName"
          type="field"
          onChange={formik.handleChange}
          value={formik.values.residentLastName}
       />

    <label htmlFor="date">Move-In Date</label>
      <input
        id="residentMoveInDate"
        name="residentMoveInDate"
        type="date"
        onChange={formik.handleChange}
        value={formik.values.date}
      />

    <label htmlFor="date">Date of Birth</label>
      <input
        id="residentDOB"
        name="residentDOB"
        type="date"
        onChange={formik.handleChange}
        value={formik.values.date}
      />

    <label>Cottage</label>
      <select
       name="cottage"
       onChange={formik.handleChange}
       style={{ display: 'block' }}
       >
       <option value="" label="Select" />
       <option value="cottage1" label="Cottage 1" />
       <option value="cottage2" label="Cottage 2" />
       <option value="cottage3" label="Cottage 3" />
       <option value="cottage4" label="Cottage 4" />
       </select>

    <label>Room Number</label>
       <input
          id="residentRoomNo"
          name="residentRoomNo"
          type="field"
          onChange={formik.handleChange}
          value={formik.values.residentRoomNo}
       />




       <label>Shift</label>
       <input
          id="shift"
          name="shift"
          type="field"
          onChange={formik.handleChange}
          value="Evening"
       />

       <label>Resident</label>
       <select
        name="name"
        onChange={formik.handleChange}
        style={{ display: 'block' }}
        >
        <option value="" label="Select" />
        <option value="tom" label="Tom" />
        <option value="dick" label="Dick" />
        <option value="harry" label="Harry" />
        <option value="lucille" label="Lucille" />
        </select>

      <label>Wash hands before / after each meal</label>
      <input
        id="washHands"
        name="washHands"
        type="checkbox"
        onChange={formik.handleChange}
        value={formik.values.checked}
        />
       <label>Brush teeth / Soak Dentures</label>
         <input
            id="brushTeethSoakDentures"
            name="brushTeethSoakDentures"
            type="checkbox"
            onChange={formik.handleChange}
            value={formik.values.checked}
        />
        <label>Remove Glasses</label>
            <input
               id="removeGlasse"
               name="removeGlasse"
               type="checkbox"
               onChange={formik.handleChange}
               value={formik.values.checked}
        />
        <label>Change Attends</label>
           <input
              id="changeAttends"
              name="changeAttends"
              type="checkbox"
              onChange={formik.handleChange}
              value={formik.values.checked}
        />
           <label>Empty catheter bag</label>
           <input
              id="emptyCatheterBag"
              name="emptyCatheterBag"
              type="checkbox"
              onChange={formik.handleChange}
              value={formik.values.checked}
           />
           <label>Remove compression stockings</label>
           <input
              id="removeCompStockings"
              name="removeCompStockings"
              type="checkbox"
              onChange={formik.handleChange}
              value={formik.values.checked}
           />
           <label>Remove hearing aid / lock in med drawer</label>
           <input
              id="removeHearAidLockInMedDrawer"
              name="removeHearAidLockInMedDrawer"
              type="checkbox"
              onChange={formik.handleChange}
              value={formik.values.checked}
           />
           <label>Wash face / pericare / put on PJ's</label>
           <input
              id="washFacePeriCarePJs"
              name="washFacePeriCarePJs"
              type="checkbox"
              onChange={formik.handleChange}
              value={formik.values.checked}
           />
           <label>Ate dinner</label>
            <select
            name="ateDinner"
            onChange={formik.handleChange}
            style={{ display: 'block' }}
            >
            <option value="" label="Select" />
            <option value="none" label="None" />
            <option value="quarter" label="Quarter" />
            <option value="half" label="Half" />
            <option value="full" label="Full" />
            </select>

            <label htmlFor="glassesOfWater">No. Glasses of Water Drank</label>
            <input
             id="glassesOfWater"
             name="glassesOfWater"
             type="number"
             onChange={formik.handleChange}
             value={formik.values.email}
           />
           <label>Rec Activity (Sing, Puzzles, Card, Exercise)</label>
           <input
              id="recActivity"
              name="recActivity"
              type="checkbox"
              onChange={formik.handleChange}
              value={formik.values.checked}
           />


        <div/>
      <button type="submit">Submit</button>
    </form>
    </>
  );
};

function ResidentAdd() {
  return <SignupForm />;
}

export default ResidentAdd;
