import React, {useState, useEffect}  from 'react';

const ResidentsList = (props) => {
    const [residents, updateResidents] = useState([]);

    useEffect (()=> {
        fetch('http://localhost:4002/api/residents')
        .then(response => response.json())
        .then(({data:residents}) => {
            updateResidents(residents)
        });
    },[]);

