//import React from 'react';

const fs = require('fs');
const AWS = require('aws-sdk');
const AWS_CONFIG = require('../../config/aws');
const FileUp = require('./testupload');


// Enter copied or downloaded access ID and secret key here
const ID = AWS_CONFIG.ID;
const SECRET = AWS_CONFIG.SECRET;

// The name of the bucket that you have created
const BUCKET_NAME = 'carecottagesnorth';

const s3 = new AWS.S3({
    accessKeyId: ID,
    secretAccessKey: SECRET
});

const filename = FileUp//'/Users/steve/Downloads/VastSeptember152020.pdf'
//const name = FileUp.name//VastSeptember152020.pdf'
const uploadFile = (fileName) => {
    // Read content from the file
    const fileContent = fs.readFileSync(fileName);

    // Setting up S3 upload parameters
    const params = {
        Bucket: BUCKET_NAME,
        Key: 'testbucket/'+fs.dir.path+fileName, // File name you want to save as in S3 // should be myfolder/filename.pdf
        Body: fileContent
    };

    // Uploading files to the bucket
    s3.upload(params, function(err, data) {
        if (err) {
            throw err;
        }

        console.log(`File uploaded successfully. ${data.Location}`);
    });
};

function FileUpload() {
    return <uploadFile />;
  }

