import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form';

class AssistiveDevices extends React.Component {
    render() {
        return (
          <form classname="container" formtext-padding="10 px">
            <div className="container" formtext-padding="10 px">

            {['Walker', 'Wheelchair', 'Prosthetic', 'Other'].map((type) => (
            <div key={`default-${type}`} className="mb-3">
      <Form.Check 
        type={type}
        id={`default-${type}`}
        label={`default ${type}`}
      />


    </div>
  ))}

            <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Assistive Devices:</Form.Label>
            <p>Select Those That Apply</p>
            <Form.Control as="select">
                <option>Yes</option>
                <option>No</option>
                </Form.Control>
            </Form.Group>
            </div>
        </form>
        );
      }
  }

  export default AssistiveDevices;
