import React, { Component } from 'react';
const {MongoClient, ObjectID} = require ('mongodb');


const residentList = () =>{
    MongoClient.connect('mongodb://SteveAdmin:Gr33n7pple5!!@cluster0-shard-00-00.asquc.mongodb.net:27017,cluster0-shard-00-01.asquc.mongodb.net:27017,cluster0-shard-00-02.asquc.mongodb.net:27017/cottagedata?ssl=true&replicaSet=atlas-ppexg2-shard-0&authSource=admin&retryWrites=true&w=majority', (err, client) =>
    {
    if(err){
        console.log('Unable to connect to MongoDB server');
    }
    console.log('Connected to MongoDB server');

    const db = client.db('cottagedata');

    db.collection('bookstore').find().toArray().then((docs) => {
    console.log('bookstore');
    console.log(JSON.stringify(docs, undefined, 2));
    },

    (err) => {
    console.log('Unable to fetch bookstore', err);
    });
    });
}

class ListResidents extends React.Component {
    state = {
      residentList
    };

    renderItems() {
      return this.state.residentList.map(item => (
        <div key={item._id}>
          <p>Name: {item.name}</p>
          <p>Price: {item.price}</p>
          {!item.purchased ? <p>Not purchased</p> : <p>Purchased</p>}
        </div>
      ));
    }

    render() {
      return <div>{this.renderItems()}</div>;
    }
  }

  ReactDOM.render(<ListResidents />, document.getElementById("root"));
