import React, { useEffect, useRef, useState } from "react";
import SignaturePad from "react-signature-canvas";
import Popup from "reactjs-popup";
import "../../../src/App.css";
import "./sigCanvas.css";

const Sig2 = props  => {
    const [imageURL, setImageURL] = useState(null); // create a state that will contain our image
    const sigCanvas = useRef({}); // create a ref using react useRef hook

    /* a function that uses the canvas ref to clear the canvas
    via a method given by react-signature-canvas */
    const clear = () => sigCanvas.current.clear();

    /* a function that uses the canvas ref to trim the canvas
    from white space via a method given by react-signature-canvas
    then saves it in our state */
    const save = () =>
        setImageURL(sigCanvas.current.getTrimmedCanvas())

    return(
        <div className="App">
            <Popup 
                modal 
                trigger={<button>Sign and Submit</button>}
                closeOnDocumentClick={false}
                >
                {close => (
                    <>
                    <SignaturePad
                    ref={sigCanvas}
                    canvasProps={{
                        className: "signatureCanvas"
                    }}
                    />
                    <p>Sign in space</p>
                    <button onClick={clear} type="submit">Clear</button>
                    <button onClick={close}type="submit">Close</button>
                    <button onClick={save} type="submit">Save</button>
                    </>
                )}
                </Popup>
        </div>
    );
}

export default Sig2;
